class investors extends React.Component {
     constructor(props) {
          super(props);
          this.state =({
               count: 0,
          })
     }


     componentDidMount(){
          this.interval = setInterval(() => {
              this.countStartups()
          }, 1000);
          
     }
     
     countStartups = () =>{
          if(this.state.count < Object.keys(startups).length){
               this.setState({count: this.state.count + 1})
          }
     }

     render(){
          return(
               <div className="Home">
                    <h1 className='Home-head'>Startvest</h1>
                    <Container className='homePage'>
                         <Row >
                              <Col className='text'>
                                   <h5 className='tagline'>We believe in your vision!</h5>
                                   <p className='homeText'>
                                        A place where ideas meets talents and funding.
                                        <p> Connect with top investors and achieve your dreams today!</p>
                                   </p>
                                   <Fade left>
                                   <Row className='home-btns'>
                                        <Col className='btn-outline'><Link to='/about'><Button>Learn More</Button></Link></Col>
                                        <Col  className='btn-fill'><Link to='/profile'><Button>Get Started<ArrowRightShort  height={25} width={25}/></Button></Link></Col>
                                   </Row>
                                   </Fade>
                              </Col>
                              <Zoom right>
                              <Col className='teamIcon'><img width={600} height={500}  src={Teampic} alt="teamates discussing"  /></Col>
                              </Zoom>
                         </Row>
                    </Container>

                    {/* Container for options for startups */}
                    <Container className='startup-home shadow-sm'>
                         <Row >
                              <Col >
                              <h5 className='tagline'>See all the top startups today</h5>
                              <p className='homeText'>Over {this.state.count} startups an</p>
                              </Col>
                         
                         <Zoom right>
                              <Col >
                              
                              {Object.values(startups).map((val, index)=>
                              <Col className='col-startup shadow' key={index}  sm={5} >
                                   <h3 className='col-startup-name' onClick={()=> {this.setState({view: 'startup', id: index})}}>{val.name}</h3>
                                   <p className='col-startup-sm'>{val.location}</p>
                                   <p className='col-startup-sm'>Est. {val.est}</p>
                                   <p>{val.description}</p>
                                   <Row>
                                        <Col className='col-startup-sm' >{val.industry}</Col>
                                        {(val.job) ? <Col className='col-startup-job' onClick={() => {this.setState({view: 'job', id: index})}}>{Object.keys(val.job).length} job</Col> :''}
                                   </Row>
                              </Col>
                    )}
                              </Col>

                         </Zoom>
                         </Row>
                    </Container>
               </div>
          )
          
     }
}
export default investors;
