import React from 'react';
import './profile.css';
import { Container, Row, Col, Form, Spinner, Button, Toast } from 'react-bootstrap';
import { Github, Google, Facebook, GeoAltFill, AppIndicator } from 'react-bootstrap-icons';

// Progress bar styles
import { CircularProgressbar  } from 'react-circular-progressbar';
import 'react-circular-progressbar/dist/styles.css';

// // Graph
// import {Chart} from 'react-charts';


// The startup and investors form
import StartForm from './startupForm';
// import InvestorForm from './investorForm';

// SVG Images used
// import TeamPic from '../images/team_3.svg';
import Login from '../images/login.svg';
import SignUp from '../images/sign_in.svg';

const investor={
          'name': 'PWZ Ventures',
          'location': 'Lagos, Nigeria',
          'details': 'Lorem ipsum dolor sit amet, consectetur  adipiscing elit. Porttito neque, risus euismod cursus tellus, tellus viverra vel fusce. Etiam fermentum mattis enim sed.',
          'industry': ['Cloud Computing',  'Machine Learning', 'Ecommerce', 'Fintech', 'Insuretech']

}
const startup={
     '1':{
          'name': 'Voltex designs',
          'est': '2019',
          'location':'Abuja, Nigeria',
          'brief': 'We aim to provide web services and cloud computing to help business scale up quickly using A.I'
     },
     '2':{
          'name': 'Alpha Tech',
          'est': '2016  ',
          'location':'Lagos, Nigeria',
          'brief': 'Lorem ipsum dolor sit amet, consectetur  adipiscing elit. Porttito neque, risus euismod cursus tellus'
     }
}


class profile extends React.Component {
     constructor (props) {
          super(props);
          this.state = ({
               user: [],
               authenticated: false,
               registered: true,//If the user is a new user
               //User is the current signed in user profile sent from the database

               // Login or signup
               signup: true,

               // progressbar
               percentage: 50,

               //Notification indicator
               show: true
          });
     }

     notification = (title, message) =>{
          return(
          <Toast onClose={() => this.setState({show: false})} show={this.state.show} className='notification'>
                                   <Toast.Header>
                                   <AppIndicator height={20} width={20} className="mr-auto"/>
                                   <strong className="mr-auto">{title}</strong>
                                   {/* <small >A second ago</small> */}
                                   </Toast.Header>
                                   <Toast.Body><b>{message}</b></Toast.Body>
                               </Toast>
                               )
     }

     handlecheckbox = (e) => {
          console.log(e.target.checked);
          this.setState({ check: e.target.checked });
     };

     handleChange = ({ target }) => {
          this.setState({ [target.name]: target.type === 'checkbox' ? target.checked : target.value });
     };


     //   Render the User Profile if exist or in session
     startupProfile = () => {
          return (
               <div classnames='s-profile-head'>
               <Container fluid>
                    <Row>
                         <Col sm={3} className='investor-column rightBorder'  > 
                              {this.notification('New feature', 'An investor is interested in y!')}
                              <div className='investor-section'>         
                                   <p className='investor-section-name'>Industry operated</p>
                                   <ul >
                                   {Object.values(investor.industry).map((val, key) => 
                                   <li className='investor-section-industry' key={key}><b>{val}</b></li>
                                   )}
                                   </ul>
                              </div>

                              <div className='investor-section'>
                                   <p className='investor-section-name'>Similar Startups</p>
                                   {Object.values(startup).map((val, ind) => 
                                   <div className='investor-startup' key={ind}>
                                   <h2 className='investor-startup-name'>{val.name}</h2>
                                   <span className='investor-light-tag'> <GeoAltFill  /> {val.location} </span>
                                   <p className='investor-startup-brief'>{val.brief}</p>
                                   </div>
                                   )}
                              </div>
                         </Col>

                         <Col className='investor-column ' >
                              <Row>
                                   <Col sm='auto'><div className='investor-image'></div></Col>
                                   <Col ><h3 className='Home-head'>{investor.name}</h3></Col>
                              </Row>
                              <span className='investor-light-tag'><GeoAltFill  /> {investor.location}</span>
                              <p>{investor.details}</p>
                              <h2>Investments details and startups invested in</h2>
                              <div>
                                   <h2>Investments </h2>

                              </div>
                              {/* <div className='investor-column-progressbar'>
                              <CircularProgressbar value={this.state.percentage} text={`${this.state.percentage}%`} />
                              </div> */}
                         </Col>
                    </Row>
               </Container>
          </div>
          );
     };

     // Render the User Profile if exist or in session
     investorprofile = () =>{ 
          return (
               <div classnames='s-profile-head'>
                    <Container fluid>
                         <Row>
                              <Col sm={3} className='investor-column rightBorder'  > 
                                   {this.notification('New feature', 'Your startup just released a new feature!')}
                                   <div className='investor-section'>         
                                        <p className='investor-section-name'>Industry operated</p>
                                        <ul >
                                        {Object.values(investor.industry).map((val, key) => 
                                        <li className='investor-section-industry' key={key}><b>{val}</b></li>
                                        )}
                                        </ul>
                                   </div>

                                   <div className='investor-section'>
                                        <p className='investor-section-name'>Similar Startups</p>
                                        {Object.values(startup).map((val, ind) => 
                                        <div className='investor-startup' key={ind}>
                                        <h2 className='investor-startup-name'>{val.name}</h2>
                                        <span className='investor-light-tag'> <GeoAltFill  /> {val.location} </span>
                                        <p className='investor-startup-brief'>{val.brief}</p>
                                        </div>
                                        )}
                                   </div>
                              </Col>

                              <Col className='investor-column ' >
                                   <Row>
                                        <Col sm='auto'><div className='investor-image'></div></Col>
                                        <Col ><h3 className='Home-head'>{investor.name}</h3></Col>
                                   </Row>
                                   <span className='investor-light-tag'><GeoAltFill  /> {investor.location}</span>
                                   <p>{investor.details}</p>
                                   <h2>Investments details and startups invested in</h2>
                                   <div>
                                        <h2>Investments </h2>

                                   </div>
                                   {/* <div className='investor-column-progressbar'>
                                   <CircularProgressbar value={this.state.percentage} text={`${this.state.percentage}%`} />
                                   </div> */}
                              </Col>
                         </Row>
                    </Container>
               </div>
          );
     }



     //   Render the login screen
     login = () => {
          return (
               <div>
                    <Container className='form '>
                         <Row className='form_box shadow-lg'>
                              <Col className='svgIcon'><img src={ Login } height={ 400 } width={ 400 } alt="Team pic svg" /></Col>
                              <Col className='form_items'>
                                   <h2>Login</h2>
                                   <Form >
                                        <Form.Row>
                                             <Form.Group as={ Col } controlId="formGridEmail">
                                                  <Form.Label>Email</Form.Label>
                                                  <Form.Control name='email' onChange={ this.handleChange } value={ this.state.email } className='shadow-sm textbox' type="email" placeholder="Enter email" />
                                             </Form.Group>
                                        </Form.Row>

                                        <Form.Row>
                                             <Form.Group as={ Col } controlId="formHorizontalPassword">
                                                  <Form.Label  > Password</Form.Label>
                                                  <Form.Control name='password' onChange={ this.handleChange } value={ this.state.password } className='shadow-sm textbox' type="password" placeholder="Password" />
                                             </Form.Group>
                                        </Form.Row>


                                        <Form.Group as={ Row } controlId="formHorizontalCheck">
                                             <Col>
                                                  <Form.Check name='check' onChange={ this.handleChange } value={ this.state.check } label="Remember me" checked={ this.state.check } />
                                             </Col>
                                             <Form.Label className='forgot'>Forgot Password?</Form.Label>
                                        </Form.Group>


                                        <Form.Group as={ Row }>
                                             <Col className='submit'>
                                                  <Button type="submit" >Login</Button>
                                             </Col>
                                        </Form.Group>
                                        { this.altLogin(false) }
                                   </Form>
                              </Col>
                         </Row>
                    </Container>
               </div>
          );
     };

     //   Render the signin screen
     Signin = () => {
          return (
               <div>
                    <Container className='form '>
                         <Row className='form_box shadow-lg'>
                              <Col className='svgIcon'><img src={ SignUp } height={ 400 } width={ 400 } alt="Team pic svg" /></Col>
                              <Col className='form_items'>
                                   <h2>Sign Up</h2>
                                   <Form >

                                        <Form.Group as={ Row } className='user_type' >
                                             <Col sm='auto'> Investor</Col>
                                             <Col sm='auto'>
                                                  <Form.Check
                                                       name='check1'
                                                       type="switch"
                                                       id="custom-switch"
                                                       onChange={ this.handleChange }
                                                       checked={ this.state.check1 }
                                                  />
                                             </Col>
                                             <Col sm='auto'>Startup</Col>
                                        </Form.Group>


                                        <Form.Row>
                                             <Form.Group as={ Col } controlId="formHorizontalPassword">
                                                  <Form.Label  > Username</Form.Label>
                                                  <Form.Control name='username' onChange={ this.handleChange } value={ this.state.username } className='shadow-sm textbox' type="text" placeholder="Enter username" />
                                             </Form.Group>
                                        </Form.Row>


                                        <Form.Row>
                                             <Form.Group as={ Col } controlId="formGridEmail">
                                                  <Form.Label>Email</Form.Label>
                                                  <Form.Control name='email' onChange={ this.handleChange } value={ this.state.email } className='shadow-sm textbox' type="email" placeholder="Enter email" />
                                             </Form.Group>
                                        </Form.Row>


                                        <Form.Row>
                                             <Form.Group as={ Col } controlId="formHorizontalPassword">
                                                  <Form.Label  > Password</Form.Label>
                                                  <Form.Control name='password' onChange={ this.handleChange } value={ this.state.password } className='shadow-sm textbox' type="password" placeholder="Password" />
                                             </Form.Group>
                                        </Form.Row>


                                        <Form.Group as={ Row } controlId="formHorizontalCheck">
                                             <Col>
                                                  <Form.Check name='check' onChange={ this.handleChange } value={ this.state.check } label="Remember me" />
                                             </Col>
                                             <Form.Label className='forgot'>Forgot Password?</Form.Label>
                                        </Form.Group>

                                        <Form.Group as={ Row }>
                                             <Col className='submit'>
                                                  <Button type="submit" onClick={ () => this.setState({ authenticated: true }) }>Sign Up</Button>
                                             </Col>
                                        </Form.Group>

                                        { this.altLogin(true) }

                                   </Form>
                              </Col>
                         </Row>
                    </Container>
               </div>
          );
     };

     altLogin = (signup = false) => {
          return (
               <div>
                    <Form.Group as={ Row } controlId="formHorizontalAltLogin">
                         <Col sm={ 4 }>{ (signup) ? 'Sign up with: ' : 'Login with: ' }</Col>
                         <Col><Github className='icons' /></Col>
                         <Col><Google className='icons' /></Col>
                         <Col><Facebook className='icons' /></Col>
                    </Form.Group>


                    {(signup) ?
                         <Form.Group as={ Row }>
                              <Col>
                                   Already have an account? <span className='forgot' onClick={ () => { this.setState({ signup: false }); } }>Login</span>
                              </Col>
                         </Form.Group>
                         :
                         <Form.Group as={ Row }>
                              <Col >
                                   Don't have an account? <span className='forgot' onClick={ () => { this.setState({ signup: true }); } }>Sign up</span>
                              </Col>
                         </Form.Group>
                    }

               </div>
          );
     };

     // Render the 
     renderview() {
          switch (this.state.authenticated) {
               default: return <div><Spinner className="load" animation='border' color='#21295C' /></div>;
               case false: return (this.state.signup) ? this.Signin() : this.login();
               case true: return (this.state.registered) ? this.investorprofile() : <StartForm />; //
          }
     }


     render() {
          return (
               <div className="profile">
                    {/* <h3 className="profile-head">Profile</h3> */ }
                    {this.renderview() }
               </div>
          );
     }
}
export default profile;
