import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './startups.css';

import {Col, Container, Row} from 'react-bootstrap';
import {ArrowLeft, CircleFill} from 'react-bootstrap-icons';

const jobs = {
     '1': {
          'logo': false,
          'name': 'Internship opportunity at voltex',
          'role': 'FullStack developer',
          'location': 'Abuja, Nigeria',
          'salary': 'Unpaid'
     }
}

const Job = ({startup, goback}) => {
     console.log(startup.job);
     return(
          <div>
               {Object.values(startup.job).map((val, ind) =>
                <Container key={ind}>
                <Row>
                    <Col sm={2}><ArrowLeft className='icon-back' width={40} height={40} onClick={goback}/></Col>
                    <Col sm='auto'><span className='logo'>Startup Logo</span></Col>
                     <Col sm='auto'>
                     <h1 className='startups-head'>{val.name}</h1>
                     <p className='job-overview'><span>{val.role}</span> <span><CircleFill className='icon-back' height={5} width={5}/></span> <span>{val.location}</span> <CircleFill className='icon-back' height={5} width={5}/>  <span>{val.salary}</span></p>
                     </Col>
                </Row>
               </Container>
               )}
              
          </div>
     )
}

export default Job;
